import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';

export default function App() {
  let todoItems = [{
    id: 1,
    tittel: "Dette må gjøres",
    beskrivelse:"Husk flekken",
    person: "trym",
    utfort: false,
    opprettet: "10.01.2020"
  },{
    id: 2,
    tittel: "Dette må gjøres2",
    beskrivelse:"Husk lekasjen",
    person: "kamil",
    utfort: false,
    opprettet: "10.01.2020"
  }]
  const [modal, setModal] = useState(false);
  const [formData, setFormData] = useState({ title: "" });
  const [todos, setTodos] = useState(todoItems);

  return (
    <div className="App">
      <Todos todos={todos} setTodos={setTodos}/>
      <CreateTodoButton modal={modal} setModal={setModal}/>
    </div>
  );
}

const CreateTodoButton = ({ modal, setModal}) =>{

  return(
    <>
      <Button variant="primary btn-" onClick={()=>modal?setModal(false):setModal(true)}>+ Legg til</Button>
    </>
  )
}

const Todos = ({todos, setTodos}) =>{ 

  const addTodos = (todo)=>{
    return <TodoCard todo={todo} removeSelf={removeSelf}/>
  }

  const removeSelf = (id)=>{
    const updatedTodos = todos.filter(todo => todo.id != id)
    setTodos(updatedTodos)
  }

  return(
    todos.map(todo => addTodos(todo))
  );
};


const TodoCard = ({todo, removeSelf})=>(

  <>
  <Card style={{ width: '18rem' }}>
      <Card.Body>
        <Card.Title>{todo.tittel}</Card.Title>
        <Card.Text>
          {todo.beskrivelse}
        </Card.Text>
        <Button variant="primary" onClick={()=>removeSelf(todo.id)}>Fullfør</Button>
      </Card.Body>
    </Card>
  </>
);
